li.script:
function jossoTabLibLogin(){
    var u = location.href;
    if (u.indexOf("_login") >= 0) return false;
    if (u.indexOf("#") >=0){
        u = u.substring(0,u.indexOf("#"));
    }
    if (u.indexOf("?") < 0) {
        u = u + "?_login=";
    }
    else {
        u = u + "&_login=";
    }
    location.href = u;
}
